import React, { Component } from "react";
import axios from "axios";
import $ from "jquery";
import Title from "./components/title";
import Main from "./components/main";

export default class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      auth: false,
      login: "",
    };
  }

  get_send_request(url) {

    let token = "Token " + localStorage.getItem("user_token");
    axios({
      method: "get",
      url: url,
      config: {
        headers: {
          Accept: "application/json",
          "Content-Type": "application/json",
          "Authorization": token,
        },
      },
    });
  }

  login_user_func = (login, password) => {
    let url = "http://127.0.0.1:8000/auth/token/login/";
    let data = { username: login, password: password };

    axios({
      method: "post",
      url: url,
      data: data,
      config: {
        headers: {
          Accept: "application/json",
          "Content-Type": "application/json",
        },
      },
    })
      .then((response) => {
        this.setState({ auth: true });
        localStorage.setItem("user_token", response.data.auth_token);
      })
      .catch((error) => {
        console.log("the error has occured: " + error);
      });
  };

  componentDidMount() {
    $("#add_task_button").click(() => {
      $("#content").children().css("display", "none");
      $(".task_add_form").slideToggle("slow");
    });
    $("#add_task_button").click(function () {
      $("#content").children().css("display", "none");
      $(".task_add_form").slideToggle("slow");
    });

    $("#register_button").click(function () {
      $("#content").children().css("display", "none");
      $(".register_form").slideToggle();
      return false;
    });

    $("#login_button").click(function () {
      $("#content").children().css("display", "none");
      $(".login_form").slideToggle();
      return false;
    });
  }

  render() {
    return (
      <>
        <Title />
        <Main login_user_func={this.login_user_func} auth={this.state.auth} />
      </>
    );
  }
}
