import React, { Component } from "react";

export default class AuthMenu extends Component {

  state = {
    auth: false
  }

  componentDidMount(){
    if(this.props.auth){
      const {auth} = this.state;
      this.setState({auth: !auth});
    }
  }



  render() {

    if (this.props.auth) {
      return (
        <div className="auth">
          <ul>
            <li>
              <a href="" id="login_button">
               Дмитрий Греков 
              </a>
            </li>
            <li>
              <a href="" id="register_button">
                Зарегестрироваться
              </a>
            </li>
          </ul>

          <button className="btn btn-primary " id="add_task_button">
            +
          </button>
        </div>
      );
    } else {
      return (
        <div className="auth">
          <ul>
            <li>
              <a href="" id="login_button">
                Войти
              </a>
            </li>
            <li>
              <a href="" id="register_button">
                Зарегестрироваться
              </a>
            </li>
          </ul>

          <button className="btn btn-primary " id="add_task_button">
            +
          </button>
        </div>
      );
    }
  }
}
