import React, { Component } from "react";

export default class LoginBlock extends Component {
  constructor(props) {
    super(props);
    this.state = {
      display: true,
    };
  }
  loginRef = React.createRef();
  passwordRef = React.createRef();

  login_user = (e) => {
    e.preventDefault();
    let login = this.loginRef.current.value;
    let password = this.passwordRef.current.value;

    this.props.login_user_func(login, password);

    this.setState({ display: !this.state.display });
  };

  render() {
    return (
      <div
        style={{
          display: this.state.display && !this.props.auth ? "block" : "none",
        }}
        className="login_form"
      >
        {this.props.login_error ? (
        <div class="alert alert-danger" role="alert">
          This is a danger alert—check it out!
        </div>): <></> }
        
        <form>
          <div className="form-group">
            <label htmlFor="login2">Логин</label>
            <input
              ref={this.loginRef}
              type="text"
              className="form-control"
              id="login2"
              aria-describedby="emailHelp"
              placeholder="Введите логин"
            />
          </div>

          <div className="form-group">
            <label htmlFor="password3">Пароль</label>
            <input
              ref={this.passwordRef}
              type="password"
              className="form-control"
              id="password3"
              placeholder="Введите пароль"
            />
          </div>

          <br />

          <button
            type="submit"
            onClick={this.login_user}
            className="btn btn-primary"
          >
            Войти
          </button>
        </form>
      </div>
    );
  }
}
