import React, { Component } from "react";

export default class RegisterBlock extends Component {
  constructor(props){
    super(props)
    this.state = {
      password_d_match: false,
      password_small: false,

    }
  }
    usernameRef = React.createRef();
    passwordRef = React.createRef();
    password2Ref = React.createRef();
    emailRef = React.createRef();

    register_user = (e) => {
      e.preventDefault()
      if(this.passwordRef.current.value != this.password2Ref.current.value){
        this.setState({password_d_match: true})
      }
      else if(this.passwordRef.current.value == this.password2Ref.current.value){
        this.setState({password_d_match: false})
      }
      if(this.passwordRef.current.value.length < 8){
        console.log('<8')
        this.setState({password_small: true})
      }
      else{
        this.setState({password_small: false})
      }



    }


  render() {

    return (
      <div className="register_form">
        <form>

        {this.state.password_d_match ? (
        <div class="alert alert-danger" role="alert">
          Ваши пароли не совпадают
        </div>): <></> }

        {this.state.password_small ? (
        <div class="alert alert-danger" role="alert">
          Пароль слишком короткий
        </div>): <></> }

          <div className="form-group">
            <label htmlFor="login">Логин</label>
            <input
              ref={this.usernameRef}
              type="text"
              className="form-control"
              id="login"
              aria-describedby="emailHelp"
              name="login"
              placeholder="Введите логин"
            />
          </div>
          <div className="form-group">
            <label htmlFor="email">Почта</label>
            <input
              ref={this.emailRef}
              type="email"
              className="form-control"
              id="email"
              placeholder="Введите почту"
              name="email"
            />
          </div>

          <div className="form-group">
            <label htmlFor="password">Пароль</label>
            <input
              ref={this.passwordRef}
              type="password"
              name="password"
              className="form-control"
              id="password"
              placeholder="Введите пароль"
            />
          </div>

          <div className="form-group">
            <label htmlFor="password2">Повторите пароль</label>
            <input
              ref={this.password2Ref}
              type="password"
              name="password1"
              className="form-control"
              id="password2"
              placeholder="Повторите пароль"
            />
          </div>
          <br />
          <button type="submit" className="btn btn-primary" onClick={this.register_user}>
            Зарегестрироваться
          </button>
        </form>
      </div>
    );
  }
}
